import { useState, useEffect } from "react";
import "./App.css";
import { db } from "./firebase-config";
import {
  collection,
  getDocs,
  addDoc,
  onSnapshot,
  updateDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

function App() {
  const [newName, setNewName] = useState("");
  const [newPrice, setNewPrice] = useState(0);
  const [newCategory, setNewCategory] = useState("");
  const [newQuantity, setNewQuantity] = useState(0);

  const [items, setItems] = useState([]);
  const itemsCollectionRef = collection(db, "item");

  const createItem = async () => {
    await addDoc(itemsCollectionRef, {
      name: newName,
      price: Number(newPrice),
      category: newCategory,
      quantity: Number(newQuantity),
    });
  };

  const updateItem = async (id, price) => {
    const itemDoc = doc(db, "item", id);
    const newFields = { price: price + 5 };
    await updateDoc(itemDoc, newFields);
  };

  const deleteItem = async (id) => {
    const itemDoc = doc(db, "item", id);
    await deleteDoc(itemDoc);
  };

  useEffect((itemsCollectionRef) => {
    const getItems = async () => {
      await itemsCollectionRef;
      onSnapshot(itemsCollectionRef, async () => {
        const data = await getDocs(itemsCollectionRef);
        setItems(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
      });
    };

    getItems();
  }, []);

  return (
    <div className="App">
      <input
        placeholder="Name"
        onChange={(event) => {
          setNewName(event.target.value);
        }}
      />
      <input
        type="number"
        placeholder="Price"
        onChange={(event) => {
          setNewPrice(event.target.value);
        }}
      />
      <input
        placeholder="Category"
        onChange={(event) => {
          setNewCategory(event.target.value);
        }}
      />
      <input
        type="number"
        placeholder="Quantity"
        onChange={(event) => {
          setNewQuantity(event.target.value);
        }}
      />

      <button onClick={createItem}> Create Item</button>
      {items.map((item) => {
        return (
          <div>
            {" "}
            <h1>Name: {item.name}</h1>
            <h1>Price: {item.price}</h1>
            <h1>Quantity: {item.quantity}</h1>
            <h1>Category: {item.category}</h1>
            <button
              onClick={() => {
                updateItem(item.id, item.price);
              }}
            >
              {" "}
              Increase Price
            </button>
            <button
              onClick={() => {
                deleteItem(item.id);
              }}
            >
              {" "}
              Delete Item
            </button>
          </div>
        );
      })}
    </div>
  );
}

export default App;
